            self.drawline1.setEnabled(True)
            for x in range(rowCnt):
                item_f = QtWidgets.QTableWidgetItem('%s' % vectF[x])
                item_m = QtWidgets.QTableWidgetItem('%s' % vectMag[x])
                self.tableWidget1.setItem(x, 0, item_f)
